mapper.select:
select
count(*)
from student

    and id = #{id,jdbcType=INTEGER}
    and name = #{name,jdbcType=VARCHAR}
    and age = #{age,jdbcType=INTEGER}
    and birthday = #{birthday,jdbcType=TIMESTAMP}
    and status = #{status,jdbcType=BIT}
    and d_name = #{dName,jdbcType=VARCHAR}
    and d_age_bitdat = #{dAgeBitdat,jdbcType=VARCHAR}

select

from student

    and id = #{id,jdbcType=INTEGER}
    and name = #{name,jdbcType=VARCHAR}
    and age = #{age,jdbcType=INTEGER}
    and birthday = #{birthday,jdbcType=TIMESTAMP}
    and status = #{status,jdbcType=BIT}
    and d_name = #{dName,jdbcType=VARCHAR}
    and d_age_bitdat = #{dAgeBitdat,jdbcType=VARCHAR}



        order by ${id} ${order}


        order by id desc



    limit ${offset} , ${limit}

mapper.delete:
delete from student
where id in

    #{id}

